import time
from random import choice, shuffle
from faker.providers import BaseProvider
from pathlib import Path
from string import ascii_uppercase, digits

class LocalList(BaseProvider):
    def __init__(self):
        animals = Animals().list()
        galaxies = Galaxies().list()
        self.content = animals
#        if (listname == ''):
#            sys.exit('The filename is not passed')
#        else:
#            try:
#                with open(listname) as file:
#                    self.content = file.readlines()
#                    self.content = list(map(lambda s: s.strip(), self.content))
#            except Exception as e:
#                print(f"ERROR loading {listname}", e)

    def llname(self):
        return choice(self.content)

    def llcompletename(self):
        return f"{self.llname()} {self.llname()}"

    def llemail(self, domain="mock", extension="xyz"):
        # TODO add random name and random domain
        email = f"{self.llname()}@{domain}{time.time()}.{extension}"
        return email.lower().replace(" ", "")

    def llfiscal_code(self):
        a = [char for char in f"{ascii_uppercase}{digits}"] 
        shuffle(a)
        return "".join(a[:16])

    def llphone(self):
        return f"{'*' * 3}-{'*' * 9}"


